}

/* Ensure the app can use full viewport height so Tailwind's h-screen works as expected */
html, body, #root {
  height: 100%;
  min-height: 100%;
}

.dark {
  --background: hsl(240, 10%, 4%);
  --foreground: hsl(240, 5%, 96%);
  --card: hsl(240, 10%, 7%);
  --card-foreground: hsl(240, 5%, 96%);
