  --sidebar-foreground: hsl(240, 5%, 96%);
  --sidebar-primary: hsl(262, 83%, 68%);
  --sidebar-primary-foreground: hsl(240, 10%, 4%);
  --sidebar-accent: hsl(240, 4%, 16%);
  --sidebar-accent-foreground: hsl(240, 5%, 96%);
  --sidebar-border: hsl(240, 4%, 18%);
  --sidebar-ring: hsl(262, 83%, 68%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground font-sans antialiased;
  }
}

@layer utilities {
  .glass-effect {
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.2);
  }

  .dark .glass-effect {
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.1);
  }

  .gradient-primary {
    background: linear-gradient(135deg, hsl(var(--primary)) 0%, hsl(213, 94%, 68%) 100%);
  }

  .gradient-card {
    background: linear-gradient(135deg, hsla(var(--primary), 0.1) 0%, hsla(213, 94%, 68%, 0.1) 100%);
  }

  .message-bubble {
    max-width: 85%;
    word-wrap: break-word;
  }

  .voice-wave {
    animation: voice-pulse 1.5s ease-in-out infinite;
  }

  .sidebar-collapsed {
    transform: translateX(-280px);
  }

  .sidebar-expanded {
    transform: translateX(0);
  }

  @media (max-width: 768px) {
    .sidebar-collapsed {
      transform: translateX(-100%);
    }
  }

  .animate-float {
    animation: float 3s ease-in-out infinite;
  }

  .animate-pulse-glow {
    animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }
}

@keyframes voice-pulse {
  0%, 100% { transform: scaleY(1); }
  50% { transform: scaleY(2); }
}

@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
}

/* Mobile Safe Zones */
.safe-area-top {
  padding-top: env(safe-area-inset-top);
}

.safe-area-bottom {
  padding-bottom: env(safe-area-inset-bottom);
}

.safe-area-left {
  padding-left: env(safe-area-inset-left);
}

.safe-area-right {
  padding-right: env(safe-area-inset-right);
}

.safe-area-inset {
  padding-top: env(safe-area-inset-top);
  padding-bottom: env(safe-area-inset-bottom);
  padding-left: env(safe-area-inset-left);
  padding-right: env(safe-area-inset-right);
}

/* Prevent content from going behind mobile UI elements */
.mobile-safe-container {
  min-height: 100vh;
  min-height: calc(100vh - env(safe-area-inset-top) - env(safe-area-inset-bottom));
  padding-top: env(safe-area-inset-top);
  padding-bottom: env(safe-area-inset-bottom);
}

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 6px;
}

::-webkit-scrollbar-track {
  @apply bg-muted;
}

::-webkit-scrollbar-thumb {
  @apply bg-border rounded-full;
}

::-webkit-scrollbar-thumb:hover {
  @apply bg-muted-foreground;
}

