import Stripe from "stripe";
import { storage } from "../storage";

// Gracefully handle missing Stripe key in development
const stripeKey = process.env.STRIPE_SECRET_KEY;
if (!stripeKey && process.env.NODE_ENV === 'production') {
  throw new Error('Missing required Stripe secret: STRIPE_SECRET_KEY');
}

const stripe = stripeKey ? new Stripe(stripeKey, {
  apiVersion: "2025-08-27.basil",
}) : null;

class StripeService {

  async createPaymentIntent(amount: number, currency: string = "usd"): Promise<Stripe.PaymentIntent> {
    try {
      if (!stripe) {
        throw new Error('Stripe is not configured. Please set STRIPE_SECRET_KEY.');
      }
      const paymentIntent = await stripe.paymentIntents.create({
        amount: Math.round(amount * 100), // Convert to cents
        currency: currency,
        automatic_payment_methods: {
          enabled: true,
        },
      });

      return paymentIntent;
    } catch (error: any) {
      console.error("Stripe payment intent error:", error);
      throw new Error("Failed to create payment intent: " + error.message);
    }
  }

  async createCustomer(email: string, name?: string): Promise<Stripe.Customer> {
    try {
      if (!stripe) {
        throw new Error('Stripe is not configured. Please set STRIPE_SECRET_KEY.');
      }
      const customer = await stripe.customers.create({
        email,
        name,
      });

      return customer;
    } catch (error: any) {
      console.error("Stripe customer creation error:", error);
      throw new Error("Failed to create customer: " + error.message);
    }
  }

  async createSubscription(userId: string, priceId: string): Promise<Stripe.Subscription> {
    try {
      const user = await storage.getUser(userId);
      if (!user) {
        throw new Error("User not found");
      }

      let customerId = user.stripeCustomerId;

      // Create customer if doesn't exist
      if (!customerId) {
        const customer = await this.createCustomer(user.email, user.displayName || user.username);
        customerId = customer.id;

        // Update user with stripe customer ID
        await storage.updateUser(userId, { stripeCustomerId: customerId });
      }

      // Create subscription
      if (!stripe) {
        throw new Error('Stripe is not configured. Please set STRIPE_SECRET_KEY.');
      }
      const subscription = await stripe.subscriptions.create({
        customer: customerId,
        items: [{
          price: priceId || process.env.STRIPE_PRICE_ID || "price_default",
        }],
        payment_behavior: 'default_incomplete',
        expand: ['latest_invoice.payment_intent'],
      });

      // Update user with subscription ID
      await storage.updateUser(userId, { 
        stripeSubscriptionId: subscription.id 
      });

      return subscription;
    } catch (error: any) {
      console.error("Stripe subscription error:", error);
      throw new Error("Failed to create subscription: " + error.message);
    }
  }

  async getSubscription(subscriptionId: string): Promise<Stripe.Subscription> {
    try {
      if (!stripe) {
        throw new Error('Stripe is not configured. Please set STRIPE_SECRET_KEY.');
      }
      const subscription = await stripe.subscriptions.retrieve(subscriptionId, {
        expand: ['latest_invoice.payment_intent']
      });

      return subscription;
    } catch (error: any) {
      console.error("Stripe get subscription error:", error);
      throw new Error("Failed to retrieve subscription: " + error.message);
    }
  }

  async updateSubscription(subscriptionId: string, updates: Stripe.SubscriptionUpdateParams): Promise<Stripe.Subscription> {
    try {
      if (!stripe) {
        throw new Error('Stripe is not configured. Please set STRIPE_SECRET_KEY.');
      }
      const subscription = await stripe.subscriptions.update(subscriptionId, updates);
      return subscription;
    } catch (error: any) {
      console.error("Stripe update subscription error:", error);
      throw new Error("Failed to update subscription: " + error.message);
    }
  }

  async cancelSubscription(subscriptionId: string): Promise<Stripe.Subscription> {
    try {
      if (!stripe) {
        throw new Error('Stripe is not configured. Please set STRIPE_SECRET_KEY.');
      }
      const subscription = await stripe.subscriptions.update(subscriptionId, {
        cancel_at_period_end: true
      });

      return subscription;
    } catch (error: any) {
      console.error("Stripe cancel subscription error:", error);
      throw new Error("Failed to cancel subscription: " + error.message);
    }
  }

  async createPrice(amount: number, currency: string = "usd", interval: "month" | "year" = "month"): Promise<Stripe.Price> {
    try {
      if (!stripe) {
        throw new Error('Stripe is not configured. Please set STRIPE_SECRET_KEY.');
      }
      const price = await stripe.prices.create({
        unit_amount: Math.round(amount * 100),
        currency,
        recurring: {
          interval,
        },
        product_data: {
          name: `Zenux AI ${interval === "month" ? "Monthly" : "Yearly"} Plan`,
        },
      });

      return price;
    } catch (error: any) {
      console.error("Stripe create price error:", error);
      throw new Error("Failed to create price: " + error.message);
    }
  }

  async handleWebhook(payload: string, signature: string): Promise<void> {
    try {
      const webhookSecret = process.env.STRIPE_WEBHOOK_SECRET;
      if (!webhookSecret) {
        throw new Error("Stripe webhook secret not configured");
      }

      if (!stripe) {
        throw new Error('Stripe is not configured. Please set STRIPE_SECRET_KEY.');
      }
      const event = stripe.webhooks.constructEvent(payload, signature, webhookSecret);

      switch (event.type) {
        case 'payment_intent.succeeded':
          const paymentIntent = event.data.object as Stripe.PaymentIntent;
          await this.handlePaymentSuccess(paymentIntent);
          break;

        case 'invoice.payment_succeeded':
          const invoice = event.data.object as Stripe.Invoice;
          await this.handleSubscriptionPayment(invoice);
          break;

        case 'customer.subscription.updated':
        case 'customer.subscription.deleted':
          const subscription = event.data.object as Stripe.Subscription;
          await this.handleSubscriptionUpdate(subscription);
          break;

        default:
          console.log(`Unhandled event type: ${event.type}`);
      }
    } catch (error: any) {
      console.error("Stripe webhook error:", error);
      throw new Error("Webhook processing failed: " + error.message);
    }
  }

  private async handlePaymentSuccess(paymentIntent: Stripe.PaymentIntent): Promise<void> {
    // Handle successful one-time payment
    console.log(`Payment succeeded: ${paymentIntent.id}`);

    // Here you could update user credits, unlock features, etc.
    // Based on payment metadata
  }

  private async handleSubscriptionPayment(invoice: Stripe.Invoice): Promise<void> {
    // Handle successful subscription payment
    console.log(`Subscription payment succeeded: ${invoice.id}`);

    if (invoice.customer && invoice.subscription) {
      // Update user's plan and reset usage counters
      const user = await this.getUserByStripeCustomerId(invoice.customer as string);
      if (user) {
        await storage.updateUser(user.id, {
          messagesUsed: 0,
          filesUploaded: 0,
          voiceMinutesUsed: 0,
          apiCallsUsed: 0,
        });
      }
    }
  }

  private async handleSubscriptionUpdate(subscription: Stripe.Subscription): Promise<void> {
    // Handle subscription updates (plan changes, cancellations, etc.)
    console.log(`Subscription updated: ${subscription.id}`);

    const user = await this.getUserByStripeCustomerId(subscription.customer as string);
    if (user) {
      const planMap: Record<string, string> = {
        'price_plus': 'plus',
        'price_pro': 'pro',
        'price_business': 'business',
      };

      const newPlan = subscription.items.data[0]?.price?.id 
        ? planMap[subscription.items.data[0].price.id] || 'free'
        : 'free';

      await storage.updateUser(user.id, { 
        plan: newPlan,
        stripeSubscriptionId: subscription.status === 'active' ? subscription.id : null
      });
    }
  }

  private async getUserByStripeCustomerId(customerId: string) {
    // This would need to be implemented in storage
    // For now, we'll return null
    return null;
  }

  // GHS to USD conversion (mock - in production, use real exchange rate API)
  convertGHSToUSD(amountGHS: number): number {
    const exchangeRate = 0.084; // 1 GHS = 0.084 USD (approximate)
    return Math.round(amountGHS * exchangeRate * 100) / 100;
  }

  // Create payment intent with GHS support
  async createGHSPaymentIntent(amountGHS: number): Promise<Stripe.PaymentIntent> {
    // Convert GHS to USD for Stripe processing
    const amountUSD = this.convertGHSToUSD(amountGHS);

    return this.createPaymentIntent(amountUSD, "usd");
  }
}

export const stripeService = new StripeService();

server/services/zenux.ts.content:
import axios from "axios";
import FormData from "form-data";

const ZENUX_API_KEY =  process.env.VITE_ZENUX_API_KEY;
const ZENUX_CHAT_API_URL = process.env.VITE_ZENUX_CHAT_API_URL;
const ZENUX_FILE_API_URL = process.env.VITE_ZENUX_FILE_API_URL;
const ZENUX_ANALYTICS_URL = process.env.VITE_ZENUX_ANALYTICS_URL;

if (!ZENUX_API_KEY) {
  throw new Error("Missing required Zenux API key: VITE_ZENUX_API_KEY");
}

interface ChatResponse {
  content: string;
  metadata?: any;
}

interface ValidationResult {
  isValid: boolean;
  confidence: number;
  reason: string;
  suggestions?: string[];
}

interface FileAnalysisResult {
  analysis: string;
  metadata?: any;
}

interface TranscriptionResult {
  text: string;
  duration?: number;
}

class ZenuxAIService {
  async generateChatResponse(message: string): Promise<ChatResponse> {
    try {
      const response = await axios.post(
        ZENUX_CHAT_API_URL!,
        {
          model: "zenux-gpt-5",
          messages: [
            {
              role: "system",
              content:
                "You are Zenux AI, an intelligent assistant that helps users with various tasks including mobile money transactions, offline AI validation, and general questions. Be helpful, accurate, and concise in your responses.",
            },
            {
              role: "user",
              content: message,
            },
          ],
          max_completion_tokens: 2048,
        },
        {
          headers: {
            Authorization: `Bearer ${ZENUX_API_KEY}`,
          },
        }
      );
      return {
        content:
          response.data.choices?.[0]?.message?.content ||
          "I apologize, but I couldn't generate a response. Please try again.",
        metadata: {
          model: "zenux-gpt-5",
          tokens: response.data.usage?.total_tokens || 0,
          timestamp: new Date().toISOString(),
        },
      };
    } catch (error: any) {
      console.error("Zenux chat error:", error);
      throw new Error("Failed to generate AI response: " + error.message);
    }
  }

  async generateImage(prompt: string): Promise<{ url: string }> {
    try {
      const response = await axios.post(
        ZENUX_CHAT_API_URL!,
        {
          model: "zenux-dalle-3",
          prompt,
          n: 1,
          size: "1024x1024",
          quality: "standard",
          type: "image",
        },
        {
          headers: {
            Authorization: `Bearer ${ZENUX_API_KEY}`,
          },
        }
      );
      return { url: response.data.data?.[0]?.url || "" };
    } catch (error: any) {
      console.error("Zenux image generation error:", error);
      throw new Error("Failed to generate image: " + error.message);
    }
  }

  async analyzeFile(file: Express.Multer.File, prompt?: string): Promise<FileAnalysisResult> {
    try {
      const formData = new FormData();
      formData.append("file", file.buffer, file.originalname);
      formData.append("prompt", prompt || "Analyze this file and provide a detailed summary of its contents.");
      const response = await axios.post(ZENUX_FILE_API_URL!, formData, {
        headers: {
          Authorization: `Bearer ${ZENUX_API_KEY}`,
          ...formData.getHeaders()
        },
      });
      return {
        analysis: response.data.analysis || "Could not analyze the file.",
        metadata: {
          fileName: file.originalname,
          fileSize: file.size,
          fileType: file.mimetype,
          timestamp: new Date().toISOString(),
        },
      };
    } catch (error: any) {
      console.error("Zenux file analysis error:", error);
      throw new Error("Failed to analyze file: " + error.message);
    }
  }

  async transcribeAudio(audioFile: Express.Multer.File): Promise<TranscriptionResult> {
    try {
      const formData = new FormData();
      formData.append("file", audioFile.buffer, audioFile.originalname);
      formData.append("model", "zenux-whisper-1");
      const response = await axios.post(ZENUX_FILE_API_URL!, formData, {
        headers: {
          Authorization: `Bearer ${ZENUX_API_KEY}`,
          ...formData.getHeaders()
        },
      });
      return {
        text: response.data.text,
        duration: response.data.duration || 0,
      };
    } catch (error: any) {
      console.error("Zenux transcription error:", error);
      throw new Error("Failed to transcribe audio: " + error.message);
    }
  }

  async validateTransaction(transactionData: any): Promise<ValidationResult> {
    try {
      const response = await axios.post(
        ZENUX_ANALYTICS_URL!,
        {
          type: "validate-transaction",
          transaction: transactionData,
        },
        {
          headers: {
            Authorization: `Bearer ${ZENUX_API_KEY}`,
          },
        }
      );
      const result = response.data;
      return {
        isValid: result.isValid || false,
        confidence: Math.max(0, Math.min(1, result.confidence || 0)),
        reason: result.reason || "Transaction validation completed",
        suggestions: result.suggestions || [],
      };
    } catch (error: any) {
      console.error("Zenux transaction validation error:", error);
      return this.basicTransactionValidation(transactionData);
    }
  }

  private basicTransactionValidation(data: any): ValidationResult {
    if (!data.amount || data.amount <= 0) {
      return {
        isValid: false,
        confidence: 0.95,
        reason: "Invalid amount: must be greater than zero",
      };
    }
    if (data.amount > 50000) {
      return {
        isValid: false,
        confidence: 0.9,
        reason: "Amount exceeds daily limit",
        suggestions: ["Split into smaller transactions", "Contact support for higher limits"],
      };
    }
    if (data.userBalance !== undefined && data.amount > data.userBalance) {
      return {
        isValid: false,
        confidence: 0.98,
        reason: "Insufficient balance",
        suggestions: ["Add funds to your account", "Reduce transaction amount"],
      };
    }
    return {
      isValid: true,
      confidence: 0.85,
      reason: "Transaction appears valid",
    };
  }

  async generateConfirmationPrompt(transactionData: any): Promise<string> {
    try {
      const response = await axios.post(
        ZENUX_ANALYTICS_URL!,
        {
          type: "confirmation-prompt",
          transaction: transactionData,
        },
        {
          headers: {
            Authorization: `Bearer ${ZENUX_API_KEY}`,
          },
        }
      );
      return response.data.prompt || `Confirm sending ${transactionData.currency} ${transactionData.amount.toFixed(2)} via ${transactionData.paymentMethod || "mobile money"}?`;
    } catch (error: any) {
      console.error("Zenux confirmation prompt error:", error);
      return `Confirm sending ${transactionData.currency} ${transactionData.amount.toFixed(2)} via ${transactionData.paymentMethod || "mobile money"}?`;
    }
  }

  async generateSmartReplies(message: string): Promise<string[]> {
    try {
      const response = await axios.post(
        ZENUX_CHAT_API_URL!,
        {
          model: "zenux-gpt-5",
          messages: [
            {
              role: "system",
              content: "Generate 3 helpful, contextually relevant smart reply suggestions for the user's message. Keep them concise and actionable. Respond in JSON format with a 'replies' array.",
            },
            {
              role: "user",
              content: `Generate smart replies for this message: "${message}"`,
            },
          ],
          response_format: { type: "json_object" },
        },
        {
          headers: {
            Authorization: `Bearer ${ZENUX_API_KEY}`,
          },
        }
      );
      const result = response.data;
      return result.replies || ["Tell me more", "How can I help?", "What would you like to know?"];
    } catch (error: any) {
      console.error("Zenux smart replies error:", error);
      // Fallback smart replies
      const lowerMessage = message.toLowerCase();
      if (lowerMessage.includes("transaction") || lowerMessage.includes("payment")) {
        return ["Need help with payments?", "Check transaction status", "View payment methods"];
      }
      if (lowerMessage.includes("error") || lowerMessage.includes("problem")) {
        return ["What specific error occurred?", "Can you share more details?", "Let me help troubleshoot"];
      }
      if (lowerMessage.includes("balance") || lowerMessage.includes("wallet")) {
        return ["Check current balance", "View transaction history", "Add funds to wallet"];
      }
      return ["Tell me more", "How can I help?", "What would you like to know?"];
    }
  }
}

export const zenuxAIService = new ZenuxAIService();

// --- Enhanced v2 ZenuxAIService ---
import { Readable } from 'stream';

class ZenuxAIV2Service {
  async chat({ message, user_id, conversation_id, files }: {
    message: string;
    user_id: string;
    conversation_id: string;
    files?: string[];
  }): Promise<Readable> {
    // Streams markdown response from /z1/chat/completions (or /api/chat)
    const response = await axios.post(
      ZENUX_CHAT_API_URL!,
      {
        message,
        user_id,
        conversation_id,
        files,
        enhanced: true,
        enhanced_v2: true
      },
      {
        headers: {
          Authorization: `Bearer ${ZENUX_API_KEY}`,
        },
        responseType: 'stream',
      }
    );
    return response.data; // Readable stream
  }

  async analyzeFile({ file, prompt, user_id, conversation_id }: {
    file: Express.Multer.File;
    prompt?: string;
    user_id: string;
    conversation_id: string;
  }): Promise<any> {
    const formData = new FormData();
    formData.append("file", file.buffer, {
      filename: file.originalname,
      contentType: file.mimetype,
    });
    if (prompt) formData.append("prompt", prompt);
    formData.append("user_id", user_id);
    formData.append("conversation_id", conversation_id);
    formData.append("enhanced_v2", "true");
    const response = await axios.post(
      "https://api.zenux.ai/api/file/analyze",
      formData,
      {
        headers: {
          Authorization: `Bearer ${ZENUX_API_KEY}`,
          ...formData.getHeaders(),
        },
      }
    );
    return response.data;
  }

  async generateImage({ prompt, user_id }: {
    prompt: string;
    user_id: string;
  }): Promise<{ url: string }[]> {
    const response = await axios.post(
      "https://api.zenux.ai/v1/images/generate",
      {
        prompt,
        model: "zenux-dalle-3",
        n: 1,
        size: "1024x1024",
        quality: "standard",
        user_id,
        enhanced_v2: true
      },
      {
        headers: {
          Authorization: `Bearer ${ZENUX_API_KEY}`,
        },
      }
    );
    return response.data.data || [];
  }

  async research({ query, user_id }: {
    query: string;
    user_id: string;
  }): Promise<any> {
    const response = await axios.post(
      "https://api.zenux.ai/api/research",
      {
        query,
        user_id,
        enhanced_v2: true
      },
      {
        headers: {
          Authorization: `Bearer ${ZENUX_API_KEY}`,
        },
      }
    );
    return response.data;
  }
}

export const zenuxAIV2Service = new ZenuxAIV2Service();

