import { useState, useCallback } from 'react';
import { useMutation, useQuery } from '@tanstack/react-query';
import { apiRequest, queryClient } from '@/lib/queryClient';
import { offlineAI } from '@/lib/offlineAI';
import { useApp } from '@/contexts/AppContext';

interface AIResponse {
  content: string;
  metadata?: any;
}

interface TransactionValidation {
  isValid: boolean;
  confidence: number;
  reason: string;
  suggestions?: string[];
}

export function useAI() {
  const { isOnline } = useApp();
  const { user } = useApp();
  const [isProcessing, setIsProcessing] = useState(false);

  // Send message to AI
  const sendMessage = useMutation({
    mutationFn: async ({ message, chatId }: { message: string; chatId: string }) => {
      if (!isOnline) {
        throw new Error('AI chat requires internet connection');
      }

      const payload: any = {
        message,
        conversation_id: chatId,
        user_id: user?.id || 'anonymous',
        enhanced: true,
        enhanced_v2: true,
      };

      const response = await apiRequest('POST', '/api/ai/chat', payload);

      return await response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/chats'] });
    },
  });

  // Generate image
  const generateImage = useMutation({
    mutationFn: async ({ prompt }: { prompt: string }) => {
      if (!isOnline) {
        throw new Error('Image generation requires internet connection');
      }

      const response = await apiRequest('POST', '/api/ai/generate-image', {
        prompt,
      });

      return await response.json();
    },
  });

  // Generate video
  const generateVideo = useMutation({
    mutationFn: async ({ prompt, duration = 5 }: { prompt: string; duration?: number }) => {
      if (!isOnline) {
        throw new Error('Video generation requires internet connection');
      }

      const response = await apiRequest('POST', '/api/ai/generate-video', {
        prompt,
        duration,
      });

      return await response.json();
    },
  });

  // Offline transaction validation
  const validateTransaction = useCallback(async (transactionData: any): Promise<TransactionValidation> => {
    setIsProcessing(true);

    try {
      if (isOnline) {
        // Use online AI validation
        const response = await apiRequest('POST', '/api/ai/validate-transaction', transactionData);
        const result = await response.json();
        return result;
      } else {
        // Use offline AI validation
        return await offlineAI.validateTransaction(transactionData);
      }
    } finally {
      setIsProcessing(false);
    }
  }, [isOnline]);

  // Generate confirmation prompt
  const generateConfirmationPrompt = useCallback(async (transactionData: any): Promise<string> => {
    if (isOnline) {
      try {
        const response = await apiRequest('POST', '/api/ai/confirmation-prompt', transactionData);
        const result = await response.json();
        return result.prompt;
      } catch {
        // Fallback to offline
        return await offlineAI.generateConfirmationPrompt(transactionData);
      }
    }

    return await offlineAI.generateConfirmationPrompt(transactionData);
  }, [isOnline]);

  // Validate message content
  const validateMessage = useCallback(async (content: string) => {
    return await offlineAI.validateMessage(content);
  }, []);

  // Generate smart replies
  const generateSmartReplies = useCallback(async (message: string): Promise<string[]> => {
    if (isOnline) {
      try {
        const response = await apiRequest('POST', '/api/ai/smart-replies', { message });
        const result = await response.json();
        return result.replies;
      } catch {
        // Fallback to offline
        return await offlineAI.generateSmartReplies(message);
      }
    }

    return await offlineAI.generateSmartReplies(message);
  }, [isOnline]);

  // Process file with AI
  const processFile = useMutation({
    mutationFn: async ({ file, prompt }: { file: File; prompt?: string }) => {
      if (!isOnline) {
        throw new Error('File processing requires internet connection');
      }

      const formData = new FormData();
      formData.append('file', file);
      if (prompt) formData.append('prompt', prompt);

      const response = await fetch('/api/ai/process-file', {
        method: 'POST',
        body: formData,
        credentials: 'include',
      });

      if (!response.ok) {
        throw new Error('File processing failed');
      }

      return await response.json();
    },
  });

  // Transcribe audio
  const transcribeAudio = useMutation({
    mutationFn: async ({ audioBlob }: { audioBlob: Blob }) => {
      if (!isOnline) {
        throw new Error('Audio transcription requires internet connection');
      }

      const formData = new FormData();
      formData.append('audio', audioBlob, 'recording.wav');

      const response = await fetch('/api/ai/transcribe', {
        method: 'POST',
        body: formData,
        credentials: 'include',
      });

      if (!response.ok) {
        throw new Error('Audio transcription failed');
      }

      return await response.json();
    },
  });

  return {
    // Mutations
    sendMessage,
    generateImage,
    generateVideo,
    processFile,
    transcribeAudio,

    // Functions
    validateTransaction,
    generateConfirmationPrompt,
    validateMessage,
    generateSmartReplies,

    // State
    isProcessing,
    isOnline,
  };
}

client/src/hooks/useOfflineStorage.ts.content:
import { useState, useCallback } from 'react';
import { offlineDb } from '@/lib/offlineDb';
import { 
  ChatMessage, 
  ChatSession, 
  TransactionRecord, 
  OfflineQueueItem, 
  AppSettings, 
  WalletBalance 
} from '@/types';

export function useOfflineStorage() {
  const [isLoading, setIsLoading] = useState(false);

  // Messages
  const saveMessage = useCallback(async (message: ChatMessage) => {
    setIsLoading(true);
    try {
      await offlineDb.put('messages', message);
    } finally {
      setIsLoading(false);
    }
  }, []);

  const getMessages = useCallback(async (chatId: string): Promise<ChatMessage[]> => {
    setIsLoading(true);
    try {
      const messages = await offlineDb.getAll('messages', 'chatId', chatId);
      return messages.sort((a, b) => new Date(a.createdAt).getTime() - new Date(b.createdAt).getTime());
    } finally {
      setIsLoading(false);
    }
  }, []);

  // Chat sessions
  const saveChat = useCallback(async (chat: ChatSession) => {
    await offlineDb.put('chats', chat);
  }, []);

  const getChats = useCallback(async (): Promise<ChatSession[]> => {
    const chats = await offlineDb.getAll('chats');
    return chats.sort((a, b) => new Date(b.lastMessageAt).getTime() - new Date(a.lastMessageAt).getTime());
  }, []);

  const updateChatTitle = useCallback(async (chatId: string, newTitle: string) => {
    const chat = await offlineDb.get('chats', chatId);
    if (chat) {
      const updatedChat = { ...chat, title: newTitle, lastMessageAt: new Date().toISOString() };
      await offlineDb.put('chats', updatedChat);
    }
  }, []);

  const deleteChat = useCallback(async (chatId: string) => {
    await offlineDb.delete('chats', chatId);
    // Also delete all messages in this chat
    const messages = await offlineDb.getAll('messages', 'chatId', chatId);
    for (const message of messages) {
      await offlineDb.delete('messages', message.id);
    }
  }, []);

  // Transactions
  const saveTransaction = useCallback(async (transaction: TransactionRecord) => {
    await offlineDb.put('transactions', transaction);
  }, []);

  const getTransactions = useCallback(async (): Promise<TransactionRecord[]> => {
    const transactions = await offlineDb.getAll('transactions');
    return transactions.sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime());
  }, []);

  const getQueuedTransactions = useCallback(async (): Promise<TransactionRecord[]> => {
    return await offlineDb.getAll('transactions', 'offlineQueued', true);
  }, []);

  // Offline queue
  const addToQueue = useCallback(async (item: OfflineQueueItem) => {
    await offlineDb.put('offlineQueue', item);
  }, []);

  const getQueue = useCallback(async (): Promise<OfflineQueueItem[]> => {
    return await offlineDb.getAll('offlineQueue', 'timestamp');
  }, []);

  const removeFromQueue = useCallback(async (itemId: string) => {
    await offlineDb.delete('offlineQueue', itemId);
  }, []);

  // Settings
  const saveSettings = useCallback(async (settings: AppSettings) => {
    await offlineDb.put('settings', { key: 'app_settings', ...settings });
  }, []);

  const getSettings = useCallback(async (): Promise<AppSettings | null> => {
    const result = await offlineDb.get('settings', 'app_settings');
    if (result) {
      const { key, ...settings } = result;
      return settings as AppSettings;
    }
    return null;
  }, []);

  // Wallets
  const saveWallet = useCallback(async (wallet: WalletBalance) => {
    await offlineDb.put('wallets', wallet);
  }, []);

  const getWallets = useCallback(async (): Promise<WalletBalance[]> => {
    return await offlineDb.getAll('wallets');
  }, []);

  // Clear all data
  const clearAllData = useCallback(async () => {
    await offlineDb.clear('messages');
    await offlineDb.clear('chats');
    await offlineDb.clear('transactions');
    await offlineDb.clear('offlineQueue');
    await offlineDb.clear('wallets');
  }, []);

  return {
    isLoading,
    // Messages
    saveMessage,
    getMessages,
    // Chats
    saveChat,
    getChats,
    updateChatTitle,
    deleteChat,
    // Transactions
    saveTransaction,
    getTransactions,
    getQueuedTransactions,
    // Queue
    addToQueue,
    getQueue,
    removeFromQueue,
    // Settings
    saveSettings,
    getSettings,
    // Wallets
    saveWallet,
    getWallets,
    // Utilities
    clearAllData,
  };
}

