import React from 'react';
import TopBar from '@/components/TopBar';
import { Info, Globe, Mail, Users, Heart, ExternalLink } from 'lucide-react';

const appInfo = {
  name: 'Zenux AI Chat',
  version: '1.2.4',
  build: '245',
};

