import React, { useState } from 'react';
import { CheckCircle, Trophy, Users, Clock } from 'lucide-react';

const challenges = [
  {
    id: 1,
    title: "JavaScript Basics",
    description: "Master fundamental JS concepts",
    participants: 2450,
    duration: "7 days",
