            <span className="text-gray-900 dark:text-gray-100 font-bold text-lg">{orderSummary.total}</span>
          </div>
        </div>
        {/* Payment Method */}
        <div className="mt-6 bg-white rounded-2xl p-4 shadow mb-6">
          <span className="text-gray-900 dark:text-gray-100 text-lg font-bold mb-4 block">Payment Method</span>
          {paymentMethods.map((method) => (
            <button
              key={method.id}
              className={`flex items-center p-4 rounded-xl mb-3 w-full text-left ${
                selectedPaymentMethod === method.id ? 'bg-blue-50 dark:bg-blue-900 border border-blue-200 dark:border-blue-700' : 'bg-gray-50 dark:bg-gray-800'
              }`}
              type="button"
              onClick={() => setSelectedPaymentMethod(method.id)}
            >
              <span className="mr-3">{method.icon}</span>
              <span className="flex-1">
                <span className="text-gray-900 dark:text-gray-100 font-semibold block">{method.title}</span>
                <span className="text-gray-600 dark:text-gray-400 text-sm block">
                  {method.balance ? `Balance: ${method.balance}` : method.lastDigits}
                </span>
              </span>
              {selectedPaymentMethod === method.id && (
                <span className="w-6 h-6 rounded-full bg-blue-600 dark:bg-blue-700 flex items-center justify-center">
                  <Check color="white" size={16} />
                </span>
              )}
            </button>
          ))}
        </div>
        {/* Security Info */}
        <div className="mt-6 bg-white rounded-2xl p-4 shadow mb-6">
          <div className="flex items-center mb-3">
            <ShieldCheck color="#2ECC71" size={24} />
            <span className="text-gray-900 dark:text-gray-100 text-lg font-bold ml-2">Secure Payment</span>
          </div>
          <span className="text-gray-600 dark:text-gray-400 block">
            Your payment information is encrypted and securely processed. <br />
            We do not store your credit card details.
          </span>
        </div>
        {/* Confirm Button */}
        <button className="mt-6 mb-8 bg-blue-600 rounded-2xl p-5 w-full flex items-center justify-center" type="button">
          <span className="text-white text-lg font-bold">Confirm Payment</span>
        </button>
      </div>
    </div>
  );
}

client/src/pages/code-execution.tsx.content:
import React, { useState } from 'react';
import { Play, ChevronDown, Copy, Trash2, Clock } from 'lucide-react';
import TopBar from '@/components/TopBar';
import { useApp } from '@/contexts/AppContext';

const CodeExecutionScreen = () => {
  const { user } = useApp();
  const [selectedLanguage, setSelectedLanguage] = useState('python');
  const [code, setCode] = useState('print("Hello, World!")');
  const [output, setOutput] = useState('Hello, World!');
  const [isLanguageSelectorOpen, setIsLanguageSelectorOpen] = useState(false);

  const languages = [
    { id: 'python', name: 'Python', color: 'bg-yellow-500' },
    { id: 'javascript', name: 'JavaScript', color: 'bg-yellow-600' },
    { id: 'sql', name: 'SQL', color: 'bg-blue-500' },
    { id: 'java', name: 'Java', color: 'bg-red-500' },
    { id: 'cpp', name: 'C++', color: 'bg-blue-700' },
    { id: 'react', name: 'React', color: 'bg-cyan-500' },
  ];

  const runCode = () => {
    // Send the code to the AI assistant to get execution explanation or simulated output
    (async () => {
      try {
        const conversationId = `code-exec-${Date.now()}`;
        const payload = {
          message: `Execute or explain this ${selectedLanguage} code:\n\n${code}`,
          conversation_id: conversationId,
          user_id: (user as any)?.id || 'anonymous',
          enhanced: true,
          enhanced_v2: true,
        };

        // Attach access token if available
        const { data: sessionData } = await (await import('@/lib/supabase')).supabase.auth.getSession();
        const token = sessionData?.session?.access_token;
        const headers: Record<string,string> = { 'Content-Type': 'application/json' };
        if (token) headers['Authorization'] = `Bearer ${token}`;

        const res = await fetch('/api/ai/chat', {
          method: 'POST',
          headers,
          body: JSON.stringify(payload),
        });

        if (!res.ok) {
          setOutput('AI execution service failed. Showing local simulated output.');
          return;
        }

        const text = await res.text();
        setOutput(text || 'No output from AI');
      } catch (err) { void err; setOutput('Failed to contact AI service. Showing simulated output.'); }
    })();
  };

  const clearOutput = () => {
    setOutput('');
  };

  const copyCode = () => {
    // In a real app, this would copy to clipboard
    console.log('Code copied to clipboard');
  };

  return (
  <div className="flex flex-col min-h-screen bg-white dark:bg-gray-900">
      <TopBar title="Code Executor" />
  <div className="flex-1 p-4 max-w-2xl mx-auto w-full pt-24">
        {/* Header */}
        <div className="mb-6">
          <h1 className="text-3xl font-bold text-gray-800">Code Executor</h1>
          <p className="text-gray-500 mt-1">Run code in multiple languages</p>
        </div>

        {/* Language Selector */}
        <div className="mb-4">
          <label className="text-gray-700 font-medium mb-2 block">Language</label>
          <button
            type="button"
            className="flex items-center justify-between bg-gray-50 dark:bg-gray-800 rounded-xl p-4 border border-gray-200 dark:border-gray-700 w-full"
            onClick={() => setIsLanguageSelectorOpen(!isLanguageSelectorOpen)}
          >
            <span className="text-gray-800 font-medium">
              {languages.find(lang => lang.id === selectedLanguage)?.name}
            </span>
            <ChevronDown
              color="#6b7280"
              size={20}
              className={`${isLanguageSelectorOpen ? 'rotate-180' : ''} transition-transform`}
            />
          </button>

          {isLanguageSelectorOpen && (
            <div className="mt-2 bg-white dark:bg-gray-800 rounded-lg border border-gray-200 dark:border-gray-700 shadow-sm">
              {languages.map((language) => (
                <button
                  key={language.id}
                  type="button"
                  className="flex items-center p-3 border-b border-gray-100 last:border-b-0 w-full"
                  onClick={() => {
                    setSelectedLanguage(language.id);
                    setIsLanguageSelectorOpen(false);
                  }}
                >
                  <span className={`w-3 h-3 rounded-full ${language.color} mr-3 inline-block`}></span>
                  <span className="text-gray-800">{language.name}</span>
                </button>
              ))}
            </div>
          )}
        </div>

        {/* Code Editor */}
        <div className="mb-4 flex-1 flex flex-col">
          <div className="flex justify-between items-center mb-2">
            <span className="text-gray-700 font-medium">Code Editor</span>
            <div className="flex">
              <button onClick={copyCode} className="mr-3 p-1" title="Copy code">
                <Copy color="#6b7280" size={18} />
              </button>
              <button onClick={() => setCode('')} className="p-1" title="Clear code">
                <Trash2 color="#6b7280" size={18} />
              </button>
            </div>
          </div>
          <textarea
            className="bg-gray-50 dark:bg-gray-800 rounded-xl border border-gray-200 dark:border-gray-700 p-4 font-mono text-gray-800 dark:text-gray-100 text-base flex-1 resize-none min-h-[120px]"
            value={code}
            onChange={e => setCode(e.target.value)}
            placeholder="Enter your code here..."
          />
        </div>

        {/* Run Button */}
        <button
          className="bg-blue-500 rounded-xl py-4 mb-4 flex items-center justify-center w-full"
          onClick={runCode}
        >
          <Play color="white" size={20} className="mr-2" />
          <span className="text-white font-bold text-lg">Run Code</span>
        </button>

        {/* Output Panel */}
        <div className="flex-1">
          <div className="flex justify-between items-center mb-2">
            <span className="text-gray-700 dark:text-gray-200 font-medium">Output</span>
            <button onClick={clearOutput} className="p-1" title="Clear output">
              <Trash2 color="#6b7280" size={18} />
            </button>
          </div>
          <div className="bg-gray-50 dark:bg-gray-800 rounded-xl border border-gray-200 dark:border-gray-700 min-h-[80px]">
            <div className="p-4">
              {output ? (
                <pre className="font-mono text-gray-800 dark:text-gray-100 whitespace-pre-wrap">{output}</pre>
              ) : (
                <div className="flex items-center py-8">
                  <Clock color="#9ca3af" size={20} className="mr-2" />
                  <span className="text-gray-400 dark:text-gray-500">Run code to see output</span>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CodeExecutionScreen;

