import React from 'react';
import TopBar from '@/components/TopBar';
import { User, Palette, Globe, Bell, Shield, BarChart3, Info, CreditCard, Download, Headphones, ChevronRight } from 'lucide-react';

const settingCategories = [
  { id: 'profile', title: 'Profile', icon: User, color: 'bg-blue-100', iconColor: '#2563EB' },
  { id: 'appearance', title: 'Appearance', icon: Palette, color: 'bg-purple-100', iconColor: '#9333EA' },
  { id: 'language', title: 'Language & Region', icon: Globe, color: 'bg-green-100', iconColor: '#16A34A' },
  { id: 'notifications', title: 'Notifications', icon: Bell, color: 'bg-orange-100', iconColor: '#EA580C' },
  { id: 'privacy', title: 'Privacy & Security', icon: Shield, color: 'bg-red-100', iconColor: '#DC2626' },
  { id: 'analytics', title: 'Usage & Analytics', icon: BarChart3, color: 'bg-cyan-100', iconColor: '#0891B2' },
  { id: 'about', title: 'About & Support', icon: Info, color: 'bg-gray-100', iconColor: '#6B7280' },
];

const quickActions = [
  { id: 'billing', title: 'Billing', icon: CreditCard, color: 'bg-indigo-100', iconColor: '#4F46E5' },
  { id: 'export', title: 'Export Data', icon: Download, color: 'bg-emerald-100', iconColor: '#059669' },
  { id: 'support', title: 'Support', icon: Headphones, color: 'bg-amber-100', iconColor: '#D97706' },
];

import { useState } from 'react';
import AppearanceSettingsPage from './appearance';
import ProfileSettingsPage from './profile';
import LanguageSettingsPage from './language';
import NotificationSettingsPage from './notifications';
import PrivacySecuritySettingsPage from './privacy-security';
import AboutSupportSettingsPage from './about-support';
import UsageAnalyticsSettingsPage from './usage-analytics';

export default function SettingsPage() {
  const [current, setCurrent] = useState<'main' | 'appearance' | 'profile' | 'language' | 'notifications' | 'privacy' | 'about' | 'usage'>('main');
  const handleBack = () => setCurrent('main');
  if (current === 'appearance') {
    return <AppearanceSettingsPage onBack={handleBack} />;
  }
  if (current === 'profile') {
    return <ProfileSettingsPage onBack={handleBack} />;
  }
  if (current === 'language') {
    return <LanguageSettingsPage onBack={handleBack} />;
  }
  if (current === 'notifications') {
    return <NotificationSettingsPage onBack={handleBack} />;
  }
  if (current === 'privacy') {
    return <PrivacySecuritySettingsPage onBack={handleBack} />;
  }
  if (current === 'about') {
    return <AboutSupportSettingsPage onBack={handleBack} />;
  }
  if (current === 'usage') {
    return <UsageAnalyticsSettingsPage onBack={handleBack} />;
  }
  return (
  <div className="flex flex-col min-h-screen bg-gray-50 dark:bg-gray-900 pt-[calc(3rem+env(safe-area-inset-top))] dark:text-white">
      <TopBar title="Settings" onBack={() => window.history.back()} />

      <div className="flex-1 px-4 py-4 max-w-2xl w-full mx-auto">
        {/* Quick Actions */}
        <div className="mb-6">
          <div className="text-gray-500 text-sm font-semibold mb-3">QUICK ACTIONS</div>
          <div className="flex flex-row gap-3">
            {quickActions.map((action) => {
              const IconComponent = action.icon;
              return (
                <button
                  key={action.id}
                  className="flex-1 bg-white rounded-xl p-4 flex flex-col items-center justify-center border border-gray-200"
                >
                  <div className={`w-12 h-12 rounded-full ${action.color} flex items-center justify-center mb-2`}>
                    <IconComponent size={24} stroke={action.iconColor} />
                  </div>
                  <div className="text-gray-800 font-medium">{action.title}</div>
                </button>
              );
            })}
          </div>
        </div>

        {/* Settings Categories */}
        <div className="mb-4">
          <div className="text-gray-500 text-sm font-semibold mb-3">SETTINGS</div>
          <div className="flex flex-row flex-wrap gap-3">
            {settingCategories.map((category) => {
              const IconComponent = category.icon;
              return (
                <button
                  key={category.id}
                  className="w-[48%] bg-white rounded-xl p-4 border border-gray-200 mb-2"
                  onClick={() => {
                    if (category.id === 'appearance') setCurrent('appearance');
                    if (category.id === 'profile') setCurrent('profile');
                    if (category.id === 'language') setCurrent('language');
                    if (category.id === 'notifications') setCurrent('notifications');
                    if (category.id === 'privacy') setCurrent('privacy');
                    if (category.id === 'about') setCurrent('about');
                    if (category.id === 'analytics') setCurrent('usage');
                  }}
                >
                  <div className="flex items-center">
                    <div className={`w-10 h-10 rounded-full ${category.color} flex items-center justify-center mr-3`}>
                      <IconComponent size={20} stroke={category.iconColor} />
                    </div>
                    <div className="text-gray-800 font-medium flex-1">{category.title}</div>
                    <ChevronRight size={20} stroke="#9CA3AF" />
                  </div>
                </button>
              );
            })}
          </div>
        </div>

        {/* Plan Info */}
        <div className="bg-white rounded-xl p-4 border border-gray-200">
          <div className="flex items-center justify-between">
            <div>
              <div className="text-gray-800 font-semibold">Current Plan</div>
              <div className="text-gray-500 text-sm">Free Plan</div>
            </div>
            <button className="bg-blue-600 rounded-full px-4 py-2 text-white font-medium">Upgrade</button>
          </div>
        </div>
      </div>
      {/* Safe area bottom padding */}
      <div className="pb-[env(safe-area-inset-bottom)]"></div>
    </div>
  );
}

client/src/pages/signup.tsx.content:
import React, { useState, useContext } from 'react';
import { useLocation } from 'wouter';
import { Eye, EyeOff, Mail, Lock, User, Facebook, Twitter, Github } from 'lucide-react';
import { AuthContext } from '../contexts/AuthContext';

export default function SignupPage() {
	const [, setLocation] = useLocation();
	const [name, setName] = useState('');
	const [email, setEmail] = useState('');
	const [password, setPassword] = useState('');
	const [showPassword, setShowPassword] = useState(false);
	const [nameError, setNameError] = useState('');
	const [emailError, setEmailError] = useState('');
	const [passwordError, setPasswordError] = useState('');
	const [termsAccepted, setTermsAccepted] = useState(false);
	const { signUp, authLoading } = useContext(AuthContext);
	const [formError, setFormError] = useState('');

	const validateForm = () => {
		let isValid = true;
		setNameError('');
		setEmailError('');
		setPasswordError('');
		if (!name) {
			setNameError('Name is required');
			isValid = false;
		} else if (name.length < 2) {
			setNameError('Name must be at least 2 characters');
			isValid = false;
		}
		if (!email) {
			setEmailError('Email is required');
			isValid = false;
		} else if (!/\S+@\S+\.\S+/.test(email)) {
			setEmailError('Please enter a valid email');
			isValid = false;
		}
		if (!password) {
			setPasswordError('Password is required');
			isValid = false;
		} else if (password.length < 6) {
			setPasswordError('Password must be at least 6 characters');
			isValid = false;
		}
		if (!termsAccepted) {
			alert('Please accept the terms and conditions');
			isValid = false;
		}
		return isValid;
	};

	const handleSignup = async () => {
		if (validateForm()) {
			setFormError('');
			try {
				const res = await signUp(name, email, password);
				if (res?.error) setFormError(res.error.message || 'Signup failed');
				else alert('Account created successfully! Please verify your email if required.');
			} catch (err: any) {
				setFormError(err?.message ?? 'Signup failed');
			}
		}
	};

	const handleSocialSignup = (provider: string) => {
		alert(`Signup with ${provider} would be implemented here`);
	};

		return (
			<div className="min-h-screen flex items-center justify-center bg-gray-50 dark:bg-gray-900 text-gray-900 dark:text-gray-100">
				<div className="w-full max-w-md px-6 py-8">
				{/* Header */}
						<div className="mb-8 text-center">
							<h1 className="text-3xl font-bold text-gray-800 dark:text-gray-100 mb-2">Create Account</h1>
							<p className="text-gray-600 dark:text-gray-400">Join us today and start your journey</p>
						</div>
				{/* Signup Form */}
			<div className="bg-white dark:bg-gray-800 rounded-2xl p-6 shadow-lg mb-6">
					{/* Name Field */}
					<div className="mb-5">
						  <div className="flex items-center border border-gray-300 dark:border-gray-700 rounded-xl px-4 py-3 bg-white dark:bg-gray-900">
							<User size={20} color="#6B7280" />
										<input
											className="flex-1 ml-3 text-gray-800 dark:text-gray-100 outline-none bg-transparent"
								type="text"
								placeholder="Full name"
								value={name}
								onChange={e => setName(e.target.value)}
								autoCapitalize="words"
							/>
						</div>
						{nameError && (
							<div className="text-red-500 text-sm mt-1 ml-1">{nameError}</div>
						)}
					</div>
					{/* Email Field */}
					<div className="mb-5">
						  <div className="flex items-center border border-gray-300 dark:border-gray-700 rounded-xl px-4 py-3 bg-white dark:bg-gray-900">
							<Mail size={20} color="#6B7280" />
										<input
											className="flex-1 ml-3 text-gray-800 dark:text-gray-100 outline-none bg-transparent"
								type="email"
								placeholder="Email address"
								value={email}
								onChange={e => setEmail(e.target.value)}
								autoCapitalize="none"
							/>
						</div>
						{emailError && (
							<div className="text-red-500 text-sm mt-1 ml-1">{emailError}</div>
						)}
					</div>
					{/* Password Field */}
					<div className="mb-4">
						  <div className="flex items-center border border-gray-300 dark:border-gray-700 rounded-xl px-4 py-3 bg-white dark:bg-gray-900">
							<Lock size={20} color="#6B7280" />
										<input
											className="flex-1 ml-3 text-gray-800 dark:text-gray-100 outline-none bg-transparent"
								type={showPassword ? 'text' : 'password'}
								placeholder="Password"
								value={password}
								onChange={e => setPassword(e.target.value)}
							/>
							<button type="button" onClick={() => setShowPassword(!showPassword)} className="ml-2">
								{showPassword ? (
									<EyeOff size={20} color="#6B7280" />
								) : (
									<Eye size={20} color="#6B7280" />
								)}
							</button>
						</div>
						{passwordError && (
							<div className="text-red-500 text-sm mt-1 ml-1">{passwordError}</div>
						)}
					</div>
					{/* Password Requirements */}
								<div className="mb-5">
									<span className="text-gray-500 dark:text-gray-400 text-sm ml-1">Password must be at least 6 characters</span>
					</div>
					{/* Terms Agreement */}
								<div className="mb-6 flex items-start">
									<button
										type="button"
										className={`h-5 w-5 rounded border-2 mr-3 mt-1 ${termsAccepted ? 'bg-blue-600 dark:bg-blue-700 border-blue-600 dark:border-blue-700' : 'border-gray-300 dark:border-gray-700'}`}
										onClick={() => setTermsAccepted(!termsAccepted)}
									>
										{termsAccepted && (
											<span className="block w-full h-full flex items-center justify-center">
												<span className="block h-1 w-3 bg-white absolute rotate-45" />
												<span className="block h-1 w-3 bg-white absolute -rotate-45" />
											</span>
										)}
									</button>
									<span className="flex-1 text-gray-700 dark:text-gray-300">
										I agree to the <span className="text-blue-600 dark:text-blue-300 font-bold">Terms of Service</span> and <span className="text-blue-600 dark:text-blue-300 font-bold">Privacy Policy</span>
									</span>
					</div>
					{/* Signup Button */}
								<button
									className="w-full bg-blue-600 dark:bg-blue-700 rounded-xl py-4 text-white font-bold text-lg mb-6"
									onClick={handleSignup}
									disabled={authLoading}
									type="button"
								>
									Create Account
								</button>
					{formError && <div className="text-red-500 text-sm mt-1">{formError}</div>}
					{/* Divider */}
								<div className="flex items-center mb-6">
									<div className="flex-1 h-px bg-gray-300 dark:bg-gray-700" />
									<span className="text-gray-500 dark:text-gray-400 mx-4">OR</span>
									<div className="flex-1 h-px bg-gray-300 dark:bg-gray-700" />
								</div>
					{/* Social Signup Buttons */}
								<div className="flex justify-between mb-8">
									<button
										className="flex-1 mx-1 bg-white dark:bg-gray-900 border border-gray-300 dark:border-gray-700 rounded-xl py-3 flex items-center justify-center"
										type="button"
										onClick={() => handleSocialSignup('Facebook')}
									>
										<Facebook size={24} color="#3B82F6" />
									</button>
									<button
										className="flex-1 mx-1 bg-white dark:bg-gray-900 border border-gray-300 dark:border-gray-700 rounded-xl py-3 flex items-center justify-center"
										type="button"
										onClick={() => handleSocialSignup('Twitter')}
									>
										<Twitter size={24} color="#3B82F6" />
									</button>
									<button
										className="flex-1 mx-1 bg-white dark:bg-gray-900 border border-gray-300 dark:border-gray-700 rounded-xl py-3 flex items-center justify-center"
										type="button"
										onClick={() => handleSocialSignup('GitHub')}
									>
										<Github size={24} color="#374151" />
									</button>
								</div>
				</div>
				{/* Login Link */}
						<div className="flex justify-center">
							<span className="text-gray-600 dark:text-gray-400">Already have an account? </span>
							<button type="button" className="text-blue-600 dark:text-blue-300 font-bold ml-1" onClick={() => setLocation('/login')}>Sign In</button>
						</div>
			</div>
			{/* Safe area bottom padding */}
			<div className="pb-[env(safe-area-inset-bottom)]"></div>
		</div>
	);
}

client/src/pages/splash.tsx.content:
import React, { useEffect, useRef } from 'react';
import { useLocation } from 'wouter';

export default function SplashScreen() {
  const [, setLocation] = useLocation();
  const fadeRef = useRef<HTMLDivElement>(null);
  const scaleRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    // Fade and scale animation
    if (fadeRef.current && scaleRef.current) {
      fadeRef.current.animate([
        { opacity: 0 },
        { opacity: 1 }
      ], {
        duration: 1000,
        fill: 'forwards',
        easing: 'ease-out'
      });
      scaleRef.current.animate([
        { transform: 'scale(0.8)' },
        { transform: 'scale(1)' }
      ], {
        duration: 1000,
        fill: 'forwards',
        easing: 'ease-out'
      });
    }
    // Auto navigate to main app after 3 seconds
    const timer = setTimeout(() => {
      setLocation('/home');
    }, 3000);
    return () => clearTimeout(timer);
  }, [setLocation]);

  return (
    <div className="min-h-screen bg-[#F4F7FA] flex items-center justify-center">
      <div
        className="w-full h-full flex items-center justify-center bg-gradient-to-br from-[#4A90E2] to-[#6A5ACD]"
        style={{ minHeight: '100vh' }}
      >
        <div ref={fadeRef} className="flex flex-col items-center justify-center" style={{ transition: 'opacity 1s' }}>
          <div ref={scaleRef} className="w-32 h-32 rounded-full bg-white flex items-center justify-center shadow-lg" style={{ transition: 'transform 1s' }}>
            <div className="w-20 h-20 rounded-full bg-gradient-to-br from-[#4A90E2] to-[#6A5ACD] flex items-center justify-center">
              <span className="text-2xl font-bold text-white font-aboutyou">Z</span>
            </div>
          </div>
          <div className="text-4xl font-bold text-white mt-6 tracking-wide font-aboutyou">
            <span className="text-white">ZeN</span>
            <span className="text-blue-200">ux</span>
          </div>
        </div>
      </div>
      {/* Safe area bottom padding */}
      <div className="pb-[env(safe-area-inset-bottom)]"></div>
    </div>
  );
}

