import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Progress } from '@/components/ui/progress';
import { useApp } from '@/contexts/AppContext';
import { Plus, MessageCircle, X, Menu } from 'lucide-react';
import { cn } from '@/lib/utils';

interface ChatSidebarProps {
  onNewChat: () => void;
  onChatSelect: (chatId: string) => void;
  onBillingClick: () => void;
}

export function ChatSidebar({ onNewChat, onChatSelect, onBillingClick }: ChatSidebarProps) {
  const { chatSessions, currentChatId, user, sidebarOpen, setSidebarOpen } = useApp();

  const handleChatClick = (chatId: string) => {
    onChatSelect(chatId);
  };

  const usagePercentage = user ? (user.plan.messagesUsed / user.plan.messagesLimit) * 100 : 0;

  return (
    <>
      {/* Overlay for mobile */}
      {sidebarOpen && (
        <div 
          className="fixed inset-0 bg-black/50 backdrop-blur-sm z-40 lg:hidden"
          onClick={() => setSidebarOpen(false)}
          data-testid="sidebar-overlay"
        />
      )}

      {/* Sidebar */}
      <div className={cn(
        "fixed inset-y-0 left-0 z-50 w-80 max-w-xs bg-card dark:bg-card border-r border-border dark:border-border transform transition-transform duration-300 ease-in-out lg:relative lg:translate-x-0",
        sidebarOpen ? "translate-x-0" : "-translate-x-full"
      )}>
        <div className="flex flex-col h-full">
          {/* Header */}
          <div className="flex items-center justify-between p-4 border-b border-border dark:border-border bg-background dark:bg-background">
            <div className="flex items-center space-x-3">
              <div className="w-8 h-8 rounded-lg bg-primary dark:bg-primary flex items-center justify-center">
                <MessageCircle className="w-4 h-4 text-primary-foreground" />
              </div>
              <span className="text-lg font-semibold text-foreground dark:text-foreground">Zenux AI</span>
            </div>
            <Button
              variant="ghost"
              size="sm"
              className="lg:hidden"
              onClick={() => setSidebarOpen(false)}
              data-testid="button-close-sidebar"
            >
              <X className="w-4 h-4" />
            </Button>
          </div>

          {/* New Chat Button */}
          <div className="p-4">
            <Button 
              className="w-full bg-primary text-primary-foreground hover:bg-primary/90 dark:bg-primary dark:text-primary-foreground dark:hover:bg-primary/90 rounded-xl py-3 font-semibold"
              onClick={onNewChat}
              data-testid="button-new-chat"
            >
              <Plus className="w-4 h-4 mr-2" />
              New Chat
            </Button>
          </div>

          {/* Chat History */}
          <ScrollArea className="flex-1 px-4">
            <div className="space-y-2">
              <div className="text-muted-foreground dark:text-muted-foreground text-xs font-semibold px-2 py-2">
                RECENT CHATS
              </div>

              {chatSessions.length === 0 ? (
                <div className="text-center py-8">
                  <MessageCircle className="w-12 h-12 mx-auto text-muted-foreground mb-3" />
                  <p className="text-sm text-muted-foreground">No chats yet</p>
                  <p className="text-xs text-muted-foreground mt-1">Start a conversation!</p>
                </div>
              ) : (
                chatSessions.map((chat) => (
                  <div
                    key={chat.id}
                    className={cn(
                      "flex items-center p-2 rounded-lg hover:bg-accent dark:hover:bg-accent cursor-pointer transition-colors",
                      currentChatId === chat.id && "bg-accent dark:bg-accent border border-primary/20 dark:border-primary/30"
                    )}
                    onClick={() => handleChatClick(chat.id)}
                    data-testid={`chat-item-${chat.id}`}
                  >
                    <div className="w-8 h-8 rounded-full bg-muted dark:bg-muted items-center justify-center mr-2 flex">
                      <span className="text-muted-foreground dark:text-muted-foreground font-medium text-sm">C</span>
                    </div>
                    <div className="flex-1">
                      <span className="text-foreground dark:text-foreground text-sm font-medium truncate block">
                        {chat.title}
                      </span>
                      <span className="text-muted-foreground dark:text-muted-foreground text-xs">
                        {new Date(chat.lastMessageAt).toLocaleTimeString([], { 
                          hour: '2-digit', 
                          minute: '2-digit' 
                        })}
                      </span>
                    </div>
                  </div>
                ))
              )}
            </div>
          </ScrollArea>

          {/* Plan Indicator */}
          <div className="p-4 border-t border-border dark:border-border">
            <div
              className="bg-muted dark:bg-muted rounded-lg p-3 cursor-pointer hover:bg-muted/80 dark:hover:bg-muted/80 transition-colors"
              onClick={onBillingClick}
            >
              <div className="text-foreground dark:text-foreground text-sm font-medium">Plan: {user?.plan?.type || 'Free'}</div>
              <div className="text-muted-foreground dark:text-muted-foreground text-xs mt-1">Upgrade for more features</div>
              {user && (
                <div className="mt-2">
                  <Progress 
                    value={usagePercentage} 
                    className="h-2" 
                  />
                  <div className="text-xs text-muted-foreground mt-1">
                    {user.plan.messagesUsed}/{user.plan.messagesLimit} messages used
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

client/src/components/chat/ChatView.tsx.content:
import { useState, useEffect, useRef } from 'react';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Button } from '@/components/ui/button';
import { MessageBubble } from './MessageBubble';
import { ChatInput } from './ChatInput';
import { VoiceRecordingModal } from './VoiceRecordingModal';
import { useApp } from '@/contexts/AppContext';
import { useAI } from '@/hooks/useAI';
import { useOfflineStorage } from '@/hooks/useOfflineStorage';
import { ChatMessage } from '@/types';
import { Wand2, MessageCircle } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';

export function ChatView() {
  const { currentChatId, user, isOnline } = useApp();
  const { sendMessage, processFile, transcribeAudio } = useAI();
  const { getMessages, saveMessage, saveChat } = useOfflineStorage();
  const { toast } = useToast();

  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [isVoiceModalOpen, setIsVoiceModalOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const scrollAreaRef = useRef<HTMLDivElement>(null);

  // Load messages for current chat
  useEffect(() => {
    if (currentChatId) {
      loadMessages();
    } else {
      setMessages([]);
    }
  }, [currentChatId]);

  // Auto scroll to bottom when new messages arrive
  useEffect(() => {
    if (scrollAreaRef.current) {
      scrollAreaRef.current.scrollTop = scrollAreaRef.current.scrollHeight;
    }
  }, [messages]);

  const loadMessages = async () => {
    if (!currentChatId) return;

    setIsLoading(true);
    try {
      const chatMessages = await getMessages(currentChatId);
      setMessages(chatMessages);
    } catch (error) {
      console.error('Failed to load messages:', error);
      toast({
        title: "Error",
        description: "Failed to load chat messages",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleSendMessage = async (content: string, type: 'text') => {
    if (!currentChatId || !user) return;

    // Check usage limits
    if (user.plan.messagesUsed >= user.plan.messagesLimit) {
      toast({
        title: "Message limit reached",
        description: `You've used all ${user.plan.messagesLimit} messages for this month. Upgrade your plan to continue.`,
        variant: "destructive",
      });
      return;
    }

    // Create user message
    const userMessage: ChatMessage = {
      id: crypto.randomUUID(),
      chatId: currentChatId,
      role: 'user',
      content,
      messageType: type,
      encrypted: false,
      aiValidated: false,
      createdAt: new Date().toISOString(),
    };

    // Add message to UI immediately
    setMessages(prev => [...prev, userMessage]);

    // Save to offline storage
    await saveMessage(userMessage);

    // Send to AI if online
    if (isOnline) {
      try {
        const response = await sendMessage.mutateAsync({
          message: content,
          chatId: currentChatId,
        });

        // Create AI response message
        const aiMessage: ChatMessage = {
          id: crypto.randomUUID(),
          chatId: currentChatId,
          role: 'assistant',
          content: response.content,
          messageType: 'text',
          encrypted: false,
          aiValidated: true,
          createdAt: new Date().toISOString(),
        };

        setMessages(prev => [...prev, aiMessage]);
        await saveMessage(aiMessage);

        // Update chat timestamp
        await saveChat({
          id: currentChatId,
          title: messages.length === 0 ? content.slice(0, 50) + '...' : `Chat ${currentChatId.slice(0, 8)}`,
          lastMessageAt: new Date().toISOString(),
          createdAt: new Date().toISOString(),
        });

      } catch (error) {
        toast({
          title: "Failed to send message",
          description: "Your message was saved offline and will be sent when connection is restored.",
          variant: "destructive",
        });
      }
    } else {
      toast({
        title: "Offline Mode",
        description: "Your message was saved and will be sent when you're back online.",
      });
    }
  };

  const handleSendFile = async (file: File) => {
    if (!currentChatId || !user) return;

    // Check file upload limits
    if (user.plan.filesUploaded >= user.plan.filesLimit) {
      toast({
        title: "File upload limit reached",
        description: `You've used all ${user.plan.filesLimit} file uploads for this month.`,
        variant: "destructive",
      });
      return;
    }

    if (!isOnline) {
      toast({
        title: "Offline Mode",
        description: "File uploads require an internet connection.",
        variant: "destructive",
      });
      return;
    }

    // Create file message
    const fileMessage: ChatMessage = {
      id: crypto.randomUUID(),
      chatId: currentChatId,
      role: 'user',
      content: `Uploaded file: ${file.name}`,
      messageType: 'file',
      metadata: {
        fileName: file.name,
        fileSize: file.size,
        fileType: file.type,
      },
      encrypted: false,
      aiValidated: false,
      createdAt: new Date().toISOString(),
    };

    setMessages(prev => [...prev, fileMessage]);
    await saveMessage(fileMessage);

    try {
      const response = await processFile.mutateAsync({ file });

      const aiMessage: ChatMessage = {
        id: crypto.randomUUID(),
        chatId: currentChatId,
        role: 'assistant',
        content: response.analysis || 'File processed successfully.',
        messageType: 'text',
        encrypted: false,
        aiValidated: true,
        createdAt: new Date().toISOString(),
      };

      setMessages(prev => [...prev, aiMessage]);
      await saveMessage(aiMessage);

    } catch (error) {
      toast({
        title: "File processing failed",
        description: "There was an error processing your file.",
        variant: "destructive",
      });
    }
  };

  const handleVoiceRecording = async (audioBlob: Blob, duration: number) => {
    if (!currentChatId || !user) return;

    // Check voice limits
    if (user.plan.voiceMinutesUsed >= user.plan.voiceMinutesLimit) {
      toast({
        title: "Voice limit reached",
        description: `You've used all ${user.plan.voiceMinutesLimit} voice minutes for this month.`,
        variant: "destructive",
      });
      return;
    }

    // Create voice message
    const voiceMessage: ChatMessage = {
      id: crypto.randomUUID(),
      chatId: currentChatId,
      role: 'user',
      content: 'Voice message',
      messageType: 'voice',
      metadata: { voiceDuration: Math.ceil(duration / 60) },
      encrypted: false,
      aiValidated: false,
      createdAt: new Date().toISOString(),
    };

    setMessages(prev => [...prev, voiceMessage]);
    await saveMessage(voiceMessage);

    if (isOnline) {
      try {
        const response = await transcribeAudio.mutateAsync({ audioBlob });

        if (response.text) {
          // Send transcribed text to AI
          handleSendMessage(response.text, 'text');
        }
      } catch (error) {
        toast({
          title: "Voice transcription failed",
          description: "Could not process your voice message.",
          variant: "destructive",
        });
      }
    }
  };

  if (!currentChatId) {
    return (
      <div className="h-full flex items-center justify-center bg-background dark:bg-background">
        <div className="text-center space-y-4">
          <div className="w-16 h-16 mx-auto bg-primary dark:bg-primary rounded-2xl flex items-center justify-center animate-float">
            <Wand2 className="w-8 h-8 text-primary-foreground" />
          </div>
          <div>
            <h2 className="text-xl font-bold text-foreground dark:text-foreground">Welcome to Zenux AI</h2>
            <p className="text-muted-foreground dark:text-muted-foreground text-sm mt-1">
              Your intelligent assistant for everything
            </p>
          </div>
          <p className="text-sm text-muted-foreground dark:text-muted-foreground max-w-md mx-auto">
            Start a new conversation or select an existing chat from the sidebar to begin.
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="h-full flex flex-col bg-background dark:bg-background">
      {/* Messages Area */}
      <ScrollArea 
        ref={scrollAreaRef} 
        className="flex-1 px-4 pt-4"
        data-testid="messages-area"
      >
        {isLoading ? (
          <div className="flex items-center justify-center h-32">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
          </div>
        ) : messages.length === 0 ? (
          <div className="flex justify-center mb-8">
            <div className="text-center space-y-3">
              <div className="w-16 h-16 mx-auto bg-primary rounded-2xl flex items-center justify-center animate-float">
                <MessageCircle className="w-8 h-8 text-primary-foreground" />
              </div>
              <div>
                <h3 className="text-lg font-semibold text-foreground">Start the conversation</h3>
                <p className="text-muted-foreground text-sm mt-1">
                  Ask me anything or upload a file to get started
                </p>
              </div>
            </div>
          </div>
        ) : (
          <div className="space-y-4 pb-4">
            {messages.map((message) => (
              <MessageBubble
                key={message.id}
                message={message}
                isUser={message.role === 'user'}
              />
            ))}
          </div>
        )}
      </ScrollArea>

      {/* Chat Input */}
      <ChatInput
        onSendMessage={handleSendMessage}
        onSendFile={handleSendFile}
        onStartVoiceRecording={() => setIsVoiceModalOpen(true)}
        disabled={sendMessage.isPending || processFile.isPending}
      />

      {/* Voice Recording Modal */}
      <VoiceRecordingModal
        open={isVoiceModalOpen}
        onClose={() => setIsVoiceModalOpen(false)}
        onSendRecording={handleVoiceRecording}
      />
    </div>
  );
}

client/src/components/chat/MessageBubble.content:
import { ChatMessage } from '@/types';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { CheckCheck, Play, FileText, Shield, Clock } from 'lucide-react';
import { cn } from '@/lib/utils';

interface MessageBubbleProps {
  message: ChatMessage;
  isUser: boolean;
}

export function MessageBubble({ message, isUser }: MessageBubbleProps) {
  const formatTime = (timestamp: string) => {
    return new Date(timestamp).toLocaleTimeString([], { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  const renderMessageContent = () => {
    switch (message.messageType) {
      case 'voice':
        return (
          <div className="flex items-center space-x-3">
            <Button 
              size="sm" 
              className="w-10 h-10 rounded-full bg-primary hover:opacity-80"
              data-testid="button-play-voice"
            >
              <Play className="w-4 h-4 fill-current" />
            </Button>
            <div className="flex-1">
              <div className="flex space-x-1 items-center h-8">
                {Array.from({ length: 7 }).map((_, i) => (
                  <div
                    key={i}
                    className="w-1 bg-primary rounded-full voice-wave"
                    style={{
                      height: `${20 + Math.random() * 20}px`,
                      animationDelay: `${i * 0.1}s`,
                    }}
                  />
                ))}
              </div>
            </div>
            <span className="text-xs text-muted-foreground">
              {message.metadata?.voiceDuration || '0:00'}
            </span>
          </div>
        );

      case 'file':
        return (
          <div className="flex items-center space-x-3 mb-2">
            <FileText className="w-5 h-5 text-primary" />
            <div>
              <p className="font-medium">
                {message.metadata?.fileName || 'Uploaded file'}
              </p>
              <p className="text-xs opacity-80">
                {message.metadata?.fileSize 
                  ? `${(message.metadata.fileSize / 1024).toFixed(1)} KB • ${message.metadata.fileType}`
                  : 'File attachment'
                }
              </p>
            </div>
          </div>
        );

      case 'image':
        return (
          <div className="mb-2">
            <img
              src={message.content}
              alt="Generated image"
              className="rounded-lg max-w-full h-auto"
              loading="lazy"
            />
          </div>
        );

      default:
        return <p>{message.content}</p>;
    }
  };

  if (isUser) {
    return (
      <div className="flex justify-end mb-4" data-testid={`message-${message.id}`}>
        <div className="bg-primary text-primary-foreground dark:bg-primary dark:text-primary-foreground p-4 rounded-2xl shadow-sm">
          {renderMessageContent()}
          {message.messageType === 'file' && message.content && (
            <p className="mt-2">{message.content}</p>
          )}
          <div className="flex items-center justify-end mt-2 text-xs text-primary-foreground/80 space-x-2">
            <span data-testid="message-time">{formatTime(message.createdAt)}</span>
            <CheckCheck className="w-4 h-4" />
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="flex mb-4" data-testid={`message-${message.id}`}>
      <div className="flex items-start space-x-3">
        <div className="w-8 h-8 rounded-full bg-primary dark:bg-primary flex items-center justify-center flex-shrink-0">
          <span className="text-primary-foreground font-bold text-sm">Z</span>
        </div>

        <div className="bg-card dark:bg-card border border-border dark:border-border p-4 rounded-2xl shadow-sm">
          {renderMessageContent()}

          {message.messageType === 'text' && message.content.includes('1.') && (
            <div className="mt-3 space-y-2 text-sm">
              {message.content.split('\n').filter(line => line.match(/^\d+\./)).map((item, index) => (
                <div key={index} className="p-2 bg-muted rounded-lg">
                  <div className="font-medium">{item.split(':')[0]}</div>
                  {item.includes(':') && (
                    <p className="text-xs text-muted-foreground mt-1">
                      {item.split(':').slice(1).join(':').trim()}
                    </p>
                  )}
                </div>
              ))}
            </div>
          )}

          <div className="flex items-center justify-between mt-4">
            <div className="flex items-center space-x-2">
              {message.aiValidated && (
                <Badge variant="outline" className="text-xs">
                  <Shield className="w-3 h-3 mr-1 text-primary" />
                  AI Validated
                </Badge>
              )}
              {message.encrypted && (
                <Badge variant="outline" className="text-xs">
                  <Shield className="w-3 h-3 mr-1 text-primary" />
                  Encrypted
                </Badge>
              )}
            </div>
            <span className="text-xs text-muted-foreground" data-testid="message-time">
              {formatTime(message.createdAt)}
            </span>
          </div>
        </div>
      </div>
    </div>
  );
}

